import * as React from 'react';
import { View, Text, StyleSheet, Platform, TouchableOpacity, TextInput, StatusBar } from 'react-native';
import Feather from 'react-native-vector-icons/Feather';
import LinearGradient from 'react-native-linear-gradient';
import * as Animatable from 'react-native-animatable';
import { AuthContext } from '../appContextFolder/Context';
import { useTheme } from 'react-native-paper';

const Login = () => {
    const { colors } = useTheme();
    const theme = useTheme();

    const [data, setData] = React.useState({
        username: '',
        password: '',
        checkUsernameChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPass: true,
    });

    const { signIn, loginNotif, updateLoginNotif } = React.useContext(AuthContext);

    const textUsernameChange = (e) => {
        updateLoginNotif();
        if (e.length !== 0) {
            setData({
                ...data,
                username: e,
                checkUsernameChange: true,
                isValidUser: true,
            })
        } else {
            setData({
                ...data,
                username: e,
                checkUsernameChange: false,
                isValidUser: false,
            })
        }
    }

    const textPasswordChange = (e) => {
        updateLoginNotif();
        if (e.length !== 0) {
            setData({
                ...data,
                password: e,
                isValidPass: true,
            })
        } else {
            setData({
                ...data,
                password: e,
                isValidPass: false,
            })
        }

    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        })
    }

    const loginHandler = (username, password) => {
        signIn(username, password);
    }

    const validUserHandler = (e) => {
        if (e.length > 0) {
            setData({
                ...data,
                isValidUser: true
            })
        } else {
            setData({
                ...data,
                isValidUser: false
            })
        }
    }


    return (
        <View style={styles.container}>
            <StatusBar
                backgroundColor="#17a2b8" barStyle="light-content"
            />
            <View style={[styles.header, {
                alignItems: 'center'
            }]}>
                <Text style={styles.text_header}></Text>
            </View>
            <Animatable.View
                animation="fadeInUpBig"
                style={[styles.footer, { backgroundColor: colors.background }]}
            >
                {
                    loginNotif === '' ?
                        null
                        : loginNotif === 'account-invalid' ?
                            < Animatable.View animation="fadeInLeft" duration={500} style={{ alignItems: 'center', justifyContent: 'center', paddingBottom: 10 }}>
                                <Text style={styles.text_warning}>You've enter wrong username or password, Try again.</Text>
                            </Animatable.View>
                            :
                            null
                }
                <Text style={[styles.text_footer, { color: colors.text }]}>Username</Text>
                <View style={styles.action}>
                    <Feather
                        name="user"
                        color={colors.text}
                        size={20}
                    />
                    <TextInput
                        placeholderTextColor="#666666"
                        placeholder="Your Username"
                        style={[styles.textInput, { color: colors.text }]}
                        autoCapitalize="none"
                        onChangeText={(e) => textUsernameChange(e)}
                        onEndEditing={(e) => validUserHandler(e.nativeEvent.text)}
                    />
                    {
                        data.checkUsernameChange ?
                            <Animatable.View
                                animation="bounceIn"
                            >
                                <Feather
                                    name="check-circle"
                                    size={20}
                                    color="green"
                                />
                            </Animatable.View>
                            : null
                    }
                </View>
                {
                    data.isValidUser ? null :
                        <Animatable.View animation="fadeInLeft" duration={500}>
                            <Text style={styles.text_warning}>Username required!</Text>
                        </Animatable.View>
                }
                <Text style={[styles.text_footer, { marginTop: 30, color: colors.text }]}>Password</Text>
                <View style={styles.action}>
                    <Feather
                        name="lock"
                        color={colors.text}
                        size={20}
                    />
                    <TextInput
                        placeholderTextColor="#666666"
                        placeholder="Your Password"
                        secureTextEntry={data.secureTextEntry}
                        style={[styles.textInput, {
                            color: colors.text
                        }]}
                        autoCapitalize="none"
                        onChangeText={(e) => textPasswordChange(e)}
                        onEndEditing={(e) => textPasswordChange(e.nativeEvent.text)}
                    />
                    <TouchableOpacity onPress={() => updateSecureTextEntry()}>
                        <Feather
                            name={data.secureTextEntry === true ? "eye" : "eye-off"}
                            size={20}
                            color={theme.dark ? '#fff' : '#000'}
                        />
                    </TouchableOpacity>
                </View>
                {
                    data.isValidPass ? null :
                        <Animatable.View animation="fadeInLeft" duration={500}>
                            <Text style={styles.text_warning}>Password required!</Text>
                        </Animatable.View>
                }
                <TouchableOpacity>
                    <Text style={{ color: '#17a2b8', marginTop: 30 }}>Forgot password?</Text>
                </TouchableOpacity>
                <View style={styles.button}>
                    <TouchableOpacity
                        style={styles.signIn}
                        onPress={() => { loginHandler(data.username, data.password) }}
                    >
                        <Text style={[styles.textSign, { color: '#fff' }]} >Login</Text>
                        {/* <LinearGradient
                            colors={['#00d4ff', '#38b9b6', '#2295dd']}
                            style={styles.signIn}
                        > */}

                        {/* </LinearGradient> */}
                    </TouchableOpacity>
                </View>
            </Animatable.View>
        </View >
    );
}

export default Login;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#17a2b8'
    },
    header: {
        flex: 1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50,
    },
    footer: {
        flex: 6,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 30
    },
    text_footer: {
        color: '#000',
        fontSize: 18
    },
    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5
    },
    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#000'
    },
    button: {
        alignItems: 'center',
        marginTop: 30
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },
    text_warning: {
        color: 'red',
    }
})
