import * as React from 'react';
import { View, Text, StyleSheet, Platform, TouchableOpacity, TextInput, StatusBar } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { color } from 'react-native-reanimated';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import {Feather, Entypo, Ionicons} from '@expo/vector-icons'
import * as yup from 'yup'
import { Formik } from 'formik'
import { styles } from '../styles/signInStyles';

const SignUp = ({navigation}) => {
    const [secureTextEntry, setSecureTextEntry] = useState(true)


    const [data, setData] = React.useState({
        username: '',
        password: '',
        checkUsernameChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPass: true,
    });

    return (
        <SafeAreaProvider style={styles.container}>
            <ScrollView showVerticalScrollIndicator={false}>
                <View style={styles.jetSpeedWordContainter}>
                    <Text style={styles.jetWord}>JET</Text>
                    <Text style={styles.speedWord}>SPEED</Text>
                </View>
                <View style={styles.welcomeWordContainer}>
                    <Text style={styles.welcome}>Welcome Back,</Text>
                    <Text style={styles.signContinue}>Sign up to continue</Text>
                </View>
                <View style={styles.formContainer}>
                    <View style={styles.fieldContainer}>
                        <Feather
                            name="user"
                            size={20}
                            style={styles.icon}
                        />
                        <TextInput
                            placeholder="Name"
                            style={{
                                color: '#fff', 
                                paddingLeft: 30, 
                                flex: 1, 
                                fontSize: 18
                            }}
                            autoCapitalize="none"
                        />
                    </View>
                    <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                        <Ionicons
                            name="location-outline"
                            color={'#fff'}
                            size={20}
                            style={{position: 'absolute'}}
                        />
                        <TextInput
                            placeholder="Address"
                            style={{
                                color: '#fff', 
                                paddingLeft: 30, 
                                flex: 1, 
                                fontSize: 18
                            }}
                            autoCapitalize="none"
                        />
                    </View>
                    <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                        <Feather
                            name="mail"
                            color={'#fff'}
                            size={20}
                            style={{position: 'absolute'}}
                        />
                        <TextInput
                            placeholder="Email"
                            style={{
                                color: '#fff', 
                                paddingLeft: 30, 
                                flex: 1, 
                                fontSize: 18
                            }}
                            autoCapitalize="none"
                        />
                    </View>
                    <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                        <Feather
                            name="smartphone"
                            color={'#fff'}
                            size={20}
                            style={{position: 'absolute'}}
                        />
                        <TextInput
                            placeholder="Contact No."
                            style={{
                                color: '#fff', 
                                paddingLeft: 30, 
                                flex: 1, 
                                fontSize: 18
                            }}
                            autoCapitalize="none"
                        />
                    </View>
                    <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                        <Feather
                            name="lock"
                            color={'#fff'}
                            size={20}
                            style={{position: 'absolute'}}
                        />
                        <TextInput
                            placeholder="Password"
                            style={{
                                color: '#fff', 
                                paddingLeft: 30, 
                                flex: 1, 
                                fontSize: 18
                            }}
                            secureTextEntry={data.secureTextEntry}
                            autoCapitalize="none"
                        />
                        <TouchableOpacity onPress={() => setData({...data, secureTextEntry: !data.secureTextEntry})}>
                            <Feather
                                name={data.secureTextEntry === true ? "eye" : "eye-off"}
                                size={20}
                                color={'#fff'}
                            />
                        </TouchableOpacity>
                    </View>
                    <View style={{backgroundColor: '#fff', height: 50, marginTop: 50, justifyContent: 'center', alignItems: 'center', borderRadius: 5}}>
                        <Text style={{fontSize: 18, color: '#000', fontWeight: 'bold'}}>
                            SIGN UP
                        </Text>
                    </View>
                    <View style={{flexDirection: 'row', alignItems: 'flex-end', justifyContent: 'center', marginTop: 40, marginBottom: 20}}>
                        <Text style={{color: '#fff', fontWeight: 'bold'}}>
                            Already have an account?
                        </Text>
                        <TouchableOpacity onPress={() => navigation.navigate('SignIn')}>
                            <Text style={{color: 'red', fontWeight: 'bold'}}> Sign in </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ScrollView>
        </SafeAreaProvider>
    );
}

export default SignUp;
