import React, { useState } from 'react';
import { View, Text, TouchableOpacity, TextInput } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import {Feather} from '@expo/vector-icons'
import * as yup from 'yup'
import { Formik } from 'formik'
import { styles } from '../styles/loginStyles';

const SignIn = ({navigation}) => {
    const [secureTextEntry, setSecureTextEntry] = useState(true)
    const loginValidationSchema = yup.object().shape({
        email: yup.string().email("Please enter valid email").required('Email Address is required'),
        password: yup.string().min(8, ({ min }) => `Password must be at least ${min} characters`).required('Password is required'),
    })

    return (
        <SafeAreaProvider style={styles.container}>
            <ScrollView showVerticalScrollIndicator={false}>
                <View style={styles.jetSpeedWordContainter}>
                    <Text style={styles.jetSpeedWord}>JET</Text>
                    <Text style={styles.jetSpeedWord,{color: '#333'}}>SPEED</Text>
                </View>
                <View style={styles.welcomeWordContainer}>
                    <Text style={styles.welcome}>Welcome Back,</Text>
                    <Text style={styles.signContinue}>Sign in to continue</Text>
                </View>
                <View style={{marginTop: 20}}>
                    <Formik
                        validationSchema={loginValidationSchema}
                        initialValues={{ email: '', password: '' }}
                        onSubmit={values => console.log(values)}
                    >
                        {({ 
                            handleChange, 
                            handleBlur, 
                            handleSubmit, 
                            values,
                            errors,
                            isValid,
                         }) => (
                        <>
                            <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                                <Feather
                                    name="mail"
                                    color={'#fff'}
                                    size={20}
                                    style={{position: 'absolute'}}
                                />
                                <TextInput
                                    name="email"
                                    placeholder="Email Address"
                                    style={{ color: '#fff',  paddingLeft: 30,  flex: 1,  fontSize: 18 }}
                                    onChangeText={handleChange('email')}
                                    onBlur={handleBlur('email')}
                                    value={values.email}
                                    keyboardType="email-address"
                                />
                            </View>
                            {errors.email &&
                                <Text style={{ fontSize: 12, color: 'red' }}>{errors.email}</Text>
                            }
                            <View style={{flexDirection: 'row', marginTop: 20, borderBottomWidth: 1, borderBottomColor: '#f2f2f2', paddingBottom: 5}}>
                                <Feather
                                    name="lock"
                                    color={'#fff'}
                                    size={20}
                                    style={{position: 'absolute'}}
                                />
                                <TextInput
                                    name="password"
                                    placeholder="Password"
                                    style={{ color: '#fff',  paddingLeft: 30,  flex: 1,  fontSize: 18 }}
                                    onChangeText={handleChange('password')}
                                    onBlur={handleBlur('password')}
                                    value={values.password}
                                    secureTextEntry={secureTextEntry}
                                    autoCapitalize="none"
                                />
                                <TouchableOpacity onPress={() => setSecureTextEntry(!secureTextEntry)}>
                                    <Feather
                                        name={secureTextEntry === true ? "eye" : "eye-off"}
                                        size={20}
                                        color={'#fff'}
                                    />
                                </TouchableOpacity>
                            </View>
                            {errors.password &&
                                <Text style={{ fontSize: 12, color: 'red' }}>{errors.password}</Text>
                            }
                            <TouchableOpacity>
                                <Text style={{ color: '#17a2b8', marginTop: 20 }}>Forgot password?</Text>
                            </TouchableOpacity>
                            <View style={{backgroundColor: '#fff', height: 50, marginTop: 50, justifyContent: 'center', alignItems: 'center', borderRadius: 5}}>
                                <TouchableOpacity onPress={handleSubmit} disabled={!isValid}>
                                    <Text style={{fontSize: 18, color: '#000', fontWeight: 'bold'}}> SIGN IN </Text>
                                </TouchableOpacity>
                            </View>
                        </>
                        )}
                    </Formik>
                    <View style={{flexDirection: 'row', alignItems: 'flex-end', justifyContent: 'center', marginTop: 40, marginBottom: 20}}>
                        <Text style={{color: '#fff', fontWeight: 'bold'}}>
                            Don't have an account?
                        </Text>
                        <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
                            <Text style={{color: 'red', fontWeight: 'bold'}}> Sign up </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ScrollView>
        </SafeAreaProvider>
    );
}

export default SignIn;
