import React, {useState} from 'react';
import { View, Text, StyleSheet, Platform, TouchableOpacity, TextInput, StatusBar } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { color } from 'react-native-reanimated';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import {Feather, Entypo, Ionicons} from '@expo/vector-icons'
import * as yup from 'yup'
import { Formik } from 'formik'
import { styles } from '../styles/signInStyles';

const SignUp = ({navigation}) => {
    const [secureTextEntry, setSecureTextEntry] = useState(true)

    return (
        <SafeAreaProvider style={styles.container}>
            <ScrollView showVerticalScrollIndicator={false}>
                <View style={styles.jetSpeedWordContainter}>
                    <Text style={styles.jetWord}>JET</Text>
                    <Text style={styles.speedWord}>SPEED</Text>
                </View>
                <View style={styles.welcomeWordContainer}>
                    <Text style={styles.welcome}>Welcome Back,</Text>
                    <Text style={styles.signContinue}>Sign up to continue</Text>
                </View>
                <View style={styles.formContainer}>
                    <Formik
                        // validationSchema={loginValidationSchema}
                        initialValues={{ email: '', password: '' }}
                        onSubmit={values => console.log(values)}
                    >
                        {({ 
                            handleChange, 
                            handleBlur, 
                            handleSubmit, 
                            values,
                            errors,
                            isValid,
                         }) => (
                            <>
                                <View style={styles.fieldContainer}>
                                    <Feather
                                        name="user"
                                        size={20}
                                        style={styles.icon}
                                    />
                                    <TextInput
                                        name="name"
                                        placeholder="Name"
                                        style={styles.field}
                                        onChangeText={handleChange('name')}
                                        onBlur={() => setFieldTouched('name')}
                                        value={values.name}
                                    />
                                </View>

                                <View style={styles.fieldContainer}>
                                    <Ionicons
                                        name="location-outline"
                                        size={20}
                                        style={styles.icon}
                                    />
                                    <TextInput
                                        name="address"
                                        placeholder="Address"
                                        style={styles.field}
                                        onChangeText={handleChange('address')}
                                        onBlur={() => setFieldTouched('address')}
                                        value={values.address}
                                    />
                                </View>

                                <View style={styles.fieldContainer}>
                                    <Feather
                                        name="mail"
                                        size={20}
                                        style={styles.icon}
                                    />
                                    <TextInput
                                        name="email"
                                        placeholder="Email Address"
                                        style={styles.field}
                                        onChangeText={handleChange('email')}
                                        onBlur={handleBlur('email')}
                                        value={values.email}
                                        keyboardType="email-address"
                                    />
                                </View>

                                <View style={styles.fieldContainer}>
                                    <Feather
                                        name="smartphone"
                                        size={20}
                                        style={styles.icon}
                                    />
                                    <TextInput
                                        name="mobile"
                                        placeholder="Mobile No."
                                        style={styles.field}
                                        onChangeText={handleChange('mobile')}
                                        onBlur={() => setFieldTouched('mobile')}
                                        value={values.mobile}
                                    />
                                </View>

                                <View style={styles.fieldContainer}>
                                    <Feather
                                        name="lock"
                                        size={20}
                                        style={styles.icon}
                                    />
                                    <TextInput
                                        name="password"
                                        placeholder="Password"
                                        style={styles.field}
                                        onChangeText={handleChange('password')}
                                        onBlur={handleBlur('password')}
                                        value={values.password}
                                        secureTextEntry={secureTextEntry}
                                        autoCapitalize="none"
                                    />
                                    <TouchableOpacity onPress={() => setSecureTextEntry(!secureTextEntry)}>
                                        <Feather
                                            name={secureTextEntry === true ? "eye" : "eye-off"}
                                            size={20}
                                            color={'#fff'}
                                        />
                                    </TouchableOpacity>
                                </View>
                                <View style={styles.signInContainer}>
                                    <TouchableOpacity>
                                        <Text style={styles.signInButton}> SIGN UP </Text>
                                    </TouchableOpacity>
                                </View>
                            </>
                        )}
                    </Formik>
                    
                    <View style={styles.signUpContainer}>
                        <Text style={styles.dontHaveAcc}>
                            Already have an account?
                        </Text>
                        <TouchableOpacity onPress={() => navigation.navigate('SignIn')}>
                            <Text style={styles.signUp}> Sign in </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ScrollView>
        </SafeAreaProvider>
    );
}

export default SignUp;
