import * as React from 'react';
import { View, Text, StyleSheet, Platform, TouchableOpacity, TextInput, StatusBar } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { color } from 'react-native-reanimated';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import {Feather} from '@expo/vector-icons'


const Login = () => {
    const [data, setData] = React.useState({
        username: '',
        password: '',
        checkUsernameChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPass: true,
    });

    return (
        <SafeAreaProvider style={{paddingHorizontal : 20, flex: 1, backgroundColor: '#000'}}>
            <ScrollView showVerticalScrollIndicator={false}>
                <View style={{flexDirection: "row", marginTop: 40}}>
                    <Text style={{fontWeight: "bold", fontSize: 22, color: '#fff'}}>JET</Text>
                    <Text style={{fontWeight: "bold", fontSize: 22, color: '#333'}}>SPEED</Text>
                </View>
                <View style={{marginTop: 70}}>
                    <Text style={{fontSize: 27, fontWeight: 'bold', color: '#fff'}}>Welcome Back,</Text>
                    <Text style={{fontSize: 19, fontWeight: 'bold', color: '#333'}}>Login to continue</Text>
                </View>
                <View style={{marginTop: 20}}>
                <View style={[styles.footer]} >
                {/* {
                    loginNotif === '' ?
                        null
                        : loginNotif === 'account-invalid' ?
                            < Animatable.View animation="fadeInLeft" duration={500} style={{ alignItems: 'center', justifyContent: 'center', paddingBottom: 10 }}>
                                <Text style={styles.text_warning}>You've enter wrong username or password, Try again.</Text>
                            </Animatable.View>
                            :
                            null
                } */}
                <Text style={[styles.text_footer]}>Username</Text>
                <View style={styles.action}>
                    <Feather
                        name="user"
                        color={'#fff'}
                        size={20}
                    />
                    <TextInput
                        placeholderTextColor="#666666"
                        placeholder="Your Username"
                        style={[styles.textInput]}
                        autoCapitalize="none"
                        onChangeText={(e) => textUsernameChange(e)}
                        onEndEditing={(e) => validUserHandler(e.nativeEvent.text)}
                    />
                    {
                        data.checkUsernameChange ?
                            <View >
                                <Feather
                                    name="check-circle"
                                    size={20}
                                    color="green"
                                />
                            </View>
                            : null
                    }
                </View>
                {
                    data.isValidUser ? null :
                        <View>
                            <Text style={styles.text_warning}>Username required!</Text>
                        </View>
                }
                <Text style={[styles.text_footer, { marginTop: 30 }]}>Password</Text>
                <View style={styles.action}>
                    <Feather
                        name="lock"
                        color={'#fff'}
                        size={20}
                    />
                    <TextInput
                        placeholderTextColor="#666666"
                        placeholder="Your Password"
                        secureTextEntry={data.secureTextEntry}
                        style={[styles.textInput]}
                        autoCapitalize="none"
                        onChangeText={(e) => textPasswordChange(e)}
                        onEndEditing={(e) => textPasswordChange(e.nativeEvent.text)}
                    />
                    <TouchableOpacity onPress={() => updateSecureTextEntry()}>
                        <Feather
                            name={data.secureTextEntry === true ? "eye" : "eye-off"}
                            size={20}
                            color={'#fff'}
                        />
                    </TouchableOpacity>
                </View>
                {
                    data.isValidPass ? null :
                        <View duration={500}>
                            <Text style={styles.text_warning}>Password required!</Text>
                        </View>
                }
                <TouchableOpacity>
                    <Text style={{ color: '#17a2b8', marginTop: 30 }}>Forgot password?</Text>
                </TouchableOpacity>
                <View style={styles.button}>
                    <TouchableOpacity
                        style={styles.signIn}
                    // onPress={() => { loginHandler(data.username, data.password) }}
                    >
                        <Text style={[styles.textSign, { color: '#fff' }]} >Login</Text>
                        {/* <LinearGradient
                            colors={['#00d4ff', '#38b9b6', '#2295dd']}
                            style={styles.signIn}
                        > */}

                        {/* </LinearGradient> */}
                    </TouchableOpacity>
                </View>
            </View>
                </View>
            </ScrollView>
        </SafeAreaProvider>
    );
}

export default Login;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#17a2b8'
    },
    header: {
        flex: 1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50,
    },
    footer: {
        flex: 6,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 30
    },
    text_footer: {
        color: '#000',
        fontSize: 18
    },
    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5
    },
    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#000'
    },
    button: {
        alignItems: 'center',
        marginTop: 30
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },
    text_warning: {
        color: 'red',
    }
})
